mapper.select:
select p.*,u.id,u.userName,u.profileImg, ifnull(l.likeCnt,0) likeCnt,ifnull(c.commentCnt,0) commentCnt from post p inner join user u on u.id = p.userId left join ( select postId, count(*) likeCnt from post_like group by postId ) l on p.postId = l.postId left join ( select postId, count(*) CommentCnt from comment group by postId ) c on c.postId = p.postId
and p.postId < #{lastId}
order by postId desc limit 5

select postId,imageName from post_img where postId in
#{id}
order by postId desc,imageId asc

SELECT p.postId, COALESCE(i.imageName, 'projectPostDefaultImage.jpg') AS imageName
FROM post p
LEFT JOIN post_img i ON p.postId = i.postId
WHERE p.userId = #{userId}
AND (i.imageId = (
  SELECT MIN(imageId)
  FROM post_img
  WHERE postId = p.postId
) OR i.imageId IS NULL);
